import React from 'react'
import { useState,useEffect } from 'react'
import {faTrash} from 'react-icons'
import { useParams } from 'react-router-dom'
import blogService from '../../services/blogService'
import Product from './Product'
const ProductWrap = () => {

  const { id }= useParams();
  
  

  const initialProducts = {
    id: null,
    title: "",
    description: "",
    features: "",
    gridfilename:[],
    author:"",
    role:"",
    createdAt: ""
  };
  const [products ,setProducts] = useState(initialProducts);


  const response =  (id)=>{
    blogService.get(id)
          .then(response => {
          setProducts(response.data);
          console.log(response.data)
        })
        .catch(e => {
          console.log(e);
        });
  }
  const pics = products.gridfilename
  useEffect(() => {
    
    
    response(id)
    console.log(products)
  }, [id]);
  return (
    <div>
      
          <Product id={products._id} title={products.title}
          description={products.description} features={products.features}
          author={products.author} date={products.createdAt} image={pics}/>
        
       
                

                
                    
                    
                    
                

      
    </div>
    
  )
}

export default ProductWrap
